import React, { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient, useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Shield, Calendar, AlertCircle, User, Fingerprint } from "lucide-react";
import { insertPromiseSchema } from "@shared/schema";
import { getAuthHeaders } from "@/lib/auth";
import { getQueryFn } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { authenticateForPromise, isWebAuthnSupported, registerWebAuthnForCurrentUser } from "@/lib/webauthn";
import type { InsertPromise } from "@shared/schema";

interface PromiseFormProps {
  onSuccess?: () => void;
}

export function PromiseForm({ onSuccess }: PromiseFormProps) {
  const [error, setError] = useState<string | null>(null);
  const [isAuthenticating, setIsAuthenticating] = useState(false);
  const [isRegistering, setIsRegistering] = useState(false);
  const [webAuthnRegistered, setWebAuthnRegistered] = useState<boolean | null>(null);
  const queryClient = useQueryClient();
  const { toast } = useToast();

  const { data: usersData } = useQuery<{ users: { id: string; username: string; role: string }[] }>({
    queryKey: ["/api/users"],
    queryFn: getQueryFn({ on401: "throw" }),
  });

  const { data: currentUser } = useQuery<{ user: { id: string; username: string } }>({
    queryKey: ["/api/auth/me"],
    queryFn: getQueryFn({ on401: "throw" }),
  });

  // Check if WebAuthn is already registered
  const { data: credentialsData } = useQuery<{ credentials: any[] }>({
    queryKey: ["/api/auth/credentials"],
    queryFn: getQueryFn({ on401: "throw" }),
    enabled: isWebAuthnSupported(),
  });

  // Update webAuthnRegistered state when credentials data changes
  React.useEffect(() => {
    if (credentialsData) {
      setWebAuthnRegistered(credentialsData.credentials.length > 0);
    }
  }, [credentialsData]);

  const registerWebAuthn = async () => {
    try {
      setIsRegistering(true);
      const result = await registerWebAuthnForCurrentUser();
      if (result.verified) {
        setWebAuthnRegistered(true);
        queryClient.invalidateQueries({ queryKey: ["/api/auth/credentials"] });
        toast({
          title: "Fingerprint registered!",
          description: "You can now create promises with your fingerprint.",
        });
      }
    } catch (error) {
      toast({
        title: "Registration failed",
        description: error instanceof Error ? error.message : "Failed to register fingerprint",
        variant: "destructive",
      });
    } finally {
      setIsRegistering(false);
    }
  };

  const form = useForm({
    resolver: zodResolver(insertPromiseSchema),
    defaultValues: {
      title: "",
      content: "",
      metadata: {},
      deliveryDate: "",
      recipientId: "",
    },
  });

  const createPromiseMutation = useMutation({
    mutationFn: async (data: InsertPromise) => {
      if (!isWebAuthnSupported()) {
        throw new Error("WebAuthn is not supported in this environment. Please use HTTPS or localhost.");
      }

      if (!currentUser?.user?.username) {
        throw new Error("User information not available");
      }

      setIsAuthenticating(true);

      try {
        // Authenticate with WebAuthn to get fingerprint data
        const authResult = await authenticateForPromise(currentUser.user.username);

        if (!authResult.verified) {
          throw new Error("WebAuthn authentication failed");
        }

        // Create promise with WebAuthn fingerprint data
        const response = await fetch("/api/promises", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            ...getAuthHeaders(),
          },
          body: JSON.stringify({
            promiseData: data,
            fingerprintData: authResult.fingerprintData,
            credentialId: authResult.credentialId
          }),
        });

        if (!response.ok) {
          const error = await response.json();
          throw new Error(error.error || "Failed to create promise");
        }

        return response.json();
      } finally {
        setIsAuthenticating(false);
      }
    },
    onSuccess: (result) => {
      queryClient.invalidateQueries({ queryKey: ["/api/promises"] });
      toast({
        title: "Promise created successfully!",
        description: "Your promise has been encrypted and secured. You can optionally sign it with WebAuthn for enhanced security.",
      });
      form.reset();
      onSuccess?.();
      setError(null);
    },
    onError: (error: Error) => {
      setError(error.message);
    },
  });

  const onSubmit = (data: any) => {
    setError(null);
    createPromiseMutation.mutate(data);
  };

  // Set minimum date to today
  const today = new Date().toISOString().split('T')[0];

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center">
          <Shield className="text-primary mr-2" />
          Create Secured Promise
        </CardTitle>
        <CardDescription>
          Your promise will be secured with your biometric fingerprint and cannot be modified once created
        </CardDescription>
      </CardHeader>
      <CardContent>
        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="title">Promise Title</Label>
            <Input
              id="title"
              {...form.register("title")}
              placeholder="e.g., Project Delivery Promise"
              disabled={createPromiseMutation.isPending}
            />
            {form.formState.errors.title && (
              <p className="text-sm text-red-600">{form.formState.errors.title.message}</p>
            )}
          </div>

          <div className="space-y-2">
            <Label htmlFor="content">Promise Content</Label>
            <Textarea
              id="content"
              {...form.register("content")}
              placeholder="I promise to deliver the project on time with all specified features..."
              rows={4}
              disabled={createPromiseMutation.isPending}
            />
            {form.formState.errors.content && (
              <p className="text-sm text-red-600">{form.formState.errors.content.message}</p>
            )}
          </div>

          <div className="space-y-2">
            <Label htmlFor="deliveryDate" className="flex items-center">
              <Calendar className="h-4 w-4 mr-1" />
              Delivery Date
            </Label>
            <Input
              id="deliveryDate"
              type="date"
              min={today}
              {...form.register("deliveryDate")}
              disabled={createPromiseMutation.isPending}
            />
            {form.formState.errors.deliveryDate && (
              <p className="text-sm text-red-600">{form.formState.errors.deliveryDate.message}</p>
            )}
          </div>

          <div className="space-y-2">
            <Label htmlFor="recipient" className="flex items-center">
              <User className="h-4 w-4 mr-1" />
              Recipient
            </Label>
            <Select
              onValueChange={(value) => form.setValue("recipientId", value)}
              disabled={createPromiseMutation.isPending}
            >
              <SelectTrigger>
                <SelectValue placeholder="Select recipient" />
              </SelectTrigger>
              <SelectContent>
                {usersData?.users?.map((user) => (
                  <SelectItem key={user.id} value={user.id}>
                    {user.username}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            <p className="text-xs text-slate-500">
              Recipient is required for promise creation
            </p>
          </div>

          {!isWebAuthnSupported() ? (
            <Alert variant="destructive">
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>
                WebAuthn is not supported in this environment. Please use HTTPS or localhost with a compatible browser.
              </AlertDescription>
            </Alert>
          ) : webAuthnRegistered === false ? (
            <Alert>
              <Fingerprint className="h-4 w-4" />
              <AlertDescription>
                You need to register your fingerprint first to create promises.
                <Button 
                  variant="link" 
                  className="ml-2 p-0 h-auto"
                  onClick={registerWebAuthn}
                  disabled={isRegistering}
                >
                  {isRegistering ? "Registering..." : "Register Fingerprint"}
                </Button>
              </AlertDescription>
            </Alert>
          ) : (
            <Alert>
              <Fingerprint className="h-4 w-4" />
              <AlertDescription>
                This promise will be secured with your biometric fingerprint and cannot be modified after creation.
              </AlertDescription>
            </Alert>
          )}

          {error && (
            <Alert variant="destructive">
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}

          <Button
            type="submit"
            className="w-full"
            disabled={createPromiseMutation.isPending || isAuthenticating || !isWebAuthnSupported() || webAuthnRegistered === false}
          >
            {isAuthenticating ? (
              <>
                <Fingerprint className="h-4 w-4 mr-2" />
                Please use your fingerprint...
              </>
            ) : createPromiseMutation.isPending ? (
              <>
                <Shield className="h-4 w-4 mr-2" />
                Creating Secured Promise...
              </>
            ) : (
              <>
                <Fingerprint className="h-4 w-4 mr-2" />
                Create Promise with Fingerprint
              </>
            )}
          </Button>
        </form>
      </CardContent>

    </Card>
  );
}

client/src/components/promises/promise-l.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Skeleton } from "@/components/ui/skeleton";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { 
  Shield, 
  Share2, 
  CheckCircle, 
  Clock,
  User,
  Calendar,
  AlertCircle,
  Eye,
  Download,
  Fingerprint
} from "lucide-react";
import { getQueryFn } from "@/lib/queryClient";
import { getAuthHeaders } from "@/lib/auth";
import { useToast } from "@/hooks/use-toast";
import { authenticateForPromise } from "@/lib/webauthn";
import { SharePromiseForm } from "@/components/promises/share-promise-form";
import { CertificateView } from "@/components/promises/certificate-view";

interface PromiseWithDetails {
  id: string;
  title: string;
  content: string;
  deliveryDate: string;
  createdAt: string;
  creator: {
    id: string;
    username: string;
  };
  shares: {
    user: {
      id: string;
      username: string;
    };
  }[];
  certificates: {
    id: string;
    publicKey: string;
  }[];
}

interface PromiseListProps {
  currentUser?: {
    id: string;
    username: string;
    role: string;
  };
}

export function PromiseList({ currentUser }: PromiseListProps) {
  const [selectedPromise, setSelectedPromise] = useState<string | null>(null);
  const [validationResult, setValidationResult] = useState<any>(null);
  const queryClient = useQueryClient();
  const { toast } = useToast();

  const authenticateForValidation = async () => {
    if (!currentUser?.username) {
      throw new Error("Current user not available");
    }

    const authResult = await authenticateForPromise(currentUser.username);
    if (!authResult.verified) {
      throw new Error("WebAuthn authentication failed");
    }
    return authResult;
  };

  const downloadMutation = useMutation({
    mutationFn: async (promiseId: string) => {
      const response = await fetch(`/api/promises/${promiseId}/download`, {
        headers: getAuthHeaders(),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || "Download failed");
      }

      return response.json();
    },
    onSuccess: (result) => {
      // Create and trigger download
      const blob = new Blob([JSON.stringify(result, null, 2)], { type: 'application/json' });
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `promise-${result.title.replace(/[^a-zA-Z0-9]/g, '_')}-${result.id}.json`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);

      toast({
        title: "Promise downloaded!",
        description: "The promise has been saved to your computer as a JSON file.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Download failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const { data: promisesData, isLoading, error } = useQuery<{ promises: PromiseWithDetails[] }>({
    queryKey: ["/api/promises"],
    queryFn: getQueryFn({ on401: "throw" }),
  });

  const validateMutation = useMutation({
    mutationFn: async (promiseId: string) => {
      // First authenticate with WebAuthn to get credential data
      const authResult = await authenticateForValidation();

      const response = await fetch(`/api/promises/${promiseId}/validate`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          ...getAuthHeaders(),
        },
        body: JSON.stringify({ credentialId: authResult.credentialId }),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || "Validation failed");
      }

      return response.json();
    },
    onSuccess: (result, promiseId) => {
      setSelectedPromise(promiseId);
      setValidationResult(result);
      toast({
        title: result.isValid ? "Certificate validated!" : "Validation failed",
        description: result.isValid 
          ? "Opening certificate viewer..."
          : "The promise certificate could not be verified.",
        variant: result.isValid ? "default" : "destructive",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Validation error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  const getPromiseStatus = (deliveryDate: string) => {
    const now = new Date();
    const delivery = new Date(deliveryDate);

    if (delivery < now) {
      return { status: "overdue", color: "destructive" as const, icon: AlertCircle };
    } else if ((delivery.getTime() - now.getTime()) < 7 * 24 * 60 * 60 * 1000) {
      return { status: "due_soon", color: "secondary" as const, icon: Clock };
    } else {
      return { status: "active", color: "default" as const, icon: CheckCircle };
    }
  };

  if (isLoading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Shield className="text-primary mr-2" />
            Your Promises
            <Skeleton className="ml-2 h-6 w-16" />
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          {[...Array(3)].map((_, i) => (
            <div key={i} className="p-4 border rounded-lg space-y-3">
              <div className="flex items-center justify-between">
                <Skeleton className="h-5 w-48" />
                <Skeleton className="h-5 w-20" />
              </div>
              <Skeleton className="h-4 w-full" />
              <div className="flex space-x-2">
                <Skeleton className="h-8 w-20" />
                <Skeleton className="h-8 w-20" />
              </div>
            </div>
          ))}
        </CardContent>
      </Card>
    );
  }

  if (error) {
    return (
      <Card>
        <CardContent className="pt-6">
          <Alert variant="destructive">
            <AlertCircle className="h-4 w-4" />
            <AlertDescription>
              Failed to load promises. Please try refreshing the page.
            </AlertDescription>
          </Alert>
        </CardContent>
      </Card>
    );
  }

  const promises = promisesData?.promises || [];

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center">
          <Shield className="text-primary mr-2" />
          Secured Promises
          <Badge variant="secondary" className="ml-2">
            {promises.length} promises
          </Badge>
        </CardTitle>
      </CardHeader>
      <CardContent>
        {promises.length === 0 ? (
          <div className="text-center py-8">
            <Shield className="h-16 w-16 text-slate-300 mx-auto mb-4" />
            <p className="text-slate-500 mb-4">No promises created yet</p>
            <p className="text-sm text-slate-400">Create your first secured promise to get started</p>
          </div>
        ) : (
          <div className="space-y-4">
            {promises.map((promise) => {
              const status = getPromiseStatus(promise.deliveryDate);
              const StatusIcon = status.icon;
              const isCreator = currentUser?.id === promise.creator.id;

              return (
                <div
                  key={promise.id}
                  className="p-4 border border-slate-200 rounded-lg hover:bg-slate-50 transition-colors space-y-3"
                >
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <div className="flex items-center space-x-2 mb-2">
                        <h3 className="font-semibold text-slate-900">{promise.title}</h3>
                        <Badge variant={status.color} className="flex items-center">
                          <StatusIcon className="h-3 w-3 mr-1" />
                          {status.status.replace('_', ' ')}
                        </Badge>
                      </div>

                      <p className="text-slate-600 text-sm mb-3 line-clamp-2">
                        {promise.content}
                      </p>

                      <div className="flex items-center space-x-4 text-sm text-slate-500">
                        <span className="flex items-center">
                          <User className="h-4 w-4 mr-1" />
                          by {promise.creator.username} {isCreator && "(you)"}
                        </span>
                        <span className="flex items-center">
                          <Calendar className="h-4 w-4 mr-1" />
                          Due: {formatDate(promise.deliveryDate)}
                        </span>
                        <span>
                          Shared with {promise.shares.length} users
                        </span>
                      </div>
                    </div>
                  </div>

                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => validateMutation.mutate(promise.id)}
                        disabled={validateMutation.isPending}
                      >
                        <Fingerprint className="h-4 w-4 mr-1" />
                        {validateMutation.isPending ? "Authenticating..." : "Validate with Fingerprint"}
                      </Button>

                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => downloadMutation.mutate(promise.id)}
                        disabled={downloadMutation.isPending}
                      >
                        <Download className="h-4 w-4 mr-1" />
                        Download
                      </Button>

                      {isCreator && (
                        <Dialog>
                          <DialogTrigger asChild>
                            <Button variant="outline" size="sm">
                              <Share2 className="h-4 w-4 mr-1" />
                              Share
                            </Button>
                          </DialogTrigger>
                          <DialogContent>
                            <DialogHeader>
                              <DialogTitle>Share Promise</DialogTitle>
                            </DialogHeader>
                            <SharePromiseForm 
                              promiseId={promise.id} 
                              onSuccess={() => {
                                queryClient.invalidateQueries({ queryKey: ["/api/promises"] });
                              }}
                            />
                          </DialogContent>
                        </Dialog>
                      )}

                      {validationResult && selectedPromise === promise.id && (
                        <Dialog open={true} onOpenChange={() => { setValidationResult(null); setSelectedPromise(null); }}>
                          <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
                            <CertificateView
                              validationResult={validationResult}
                              onClose={() => { setValidationResult(null); setSelectedPromise(null); }}
                            />
                          </DialogContent>
                        </Dialog>
                      )}
                    </div>

                    <div className="text-xs text-slate-400">
                      Created: {formatDate(promise.createdAt)}
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

